schemas:
DEFINE TABLE OVERWRITE area SCHEMALESS
    PERMISSIONS FOR
        SELECT, CREATE, UPDATE, DELETE WHERE $auth;

DEFINE FIELD OVERWRITE identifier ON area;
DEFINE FIELD OVERWRITE name ON area;
DEFINE FIELD OVERWRITE area ON area;

DEFINE TABLE OVERWRITE items TYPE RELATION IN area OUT item
PERMISSIONS FOR
        CREATE, UPDATE, DELETE WHERE $auth;

DEFINE TABLE OVERWRITE game SCHEMAFULL
    PERMISSIONS
        FOR select
            WHERE private = false
            OR created_by.id = $auth.id
        FOR create, update, delete
            WHERE created_by.id = $auth.id;

DEFINE FIELD OVERWRITE identifier ON game;
DEFINE FIELD OVERWRITE name ON game;
DEFINE FIELD OVERWRITE status ON game;
DEFINE FIELD OVERWRITE day ON game;
DEFINE FIELD OVERWRITE created_by ON game VALUE $auth READONLY;
DEFINE FIELD OVERWRITE private ON game DEFAULT true;

DEFINE TABLE OVERWRITE areas TYPE RELATION IN game OUT area ENFORCED
PERMISSIONS FOR
        SELECT, CREATE, UPDATE, DELETE WHERE $auth;

DEFINE TABLE OVERWRITE item SCHEMALESS
    PERMISSIONS FOR
        CREATE, SELECT, UPDATE, DELETE WHERE $auth;

DEFINE FIELD OVERWRITE identifier ON item;
DEFINE FIELD OVERWRITE name ON item;
DEFINE FIELD OVERWRITE item_type ON item;
DEFINE FIELD OVERWRITE quantity ON item;
DEFINE FIELD OVERWRITE attribute ON item;
DEFINE FIELD OVERWRITE effect ON item;

DEFINE TABLE OVERWRITE message SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR update, delete NONE
        FOR create WHERE $auth;

DEFINE FIELD OVERWRITE identifier ON message;
DEFINE FIELD OVERWRITE source ON message TYPE object;
DEFINE FIELD OVERWRITE source.type ON message TYPE string;
DEFINE FIELD OVERWRITE source.value ON message TYPE string;
DEFINE FIELD OVERWRITE game_day ON message TYPE number;
DEFINE FIELD OVERWRITE subject ON message TYPE string;
DEFINE FIELD OVERWRITE timestamp ON message TYPE number;
DEFINE FIELD OVERWRITE content ON message TYPE string;

-- Define indexes for querying
DEFINE INDEX OVERWRITE message_timestamp ON message FIELDS timestamp;
DEFINE INDEX OVERWRITE message_game_day ON message FIELDS game_day;
DEFINE INDEX OVERWRITE message_source_type ON message FIELDS source.type;
DEFINE INDEX OVERWRITE message_game_id ON message FIELDS source.value;

-- Helper functions for common queries
DEFINE FUNCTION OVERWRITE fn::get_messages_by_day($day: number) {
    RETURN SELECT * FROM message
    WHERE game_day = $day
    ORDER BY timestamp;
};

DEFINE FUNCTION OVERWRITE fn::get_messages_by_source_type($source_type: string) {
    RETURN SELECT * FROM message
    WHERE source.type = $source_type
    ORDER BY timestamp;
};

DEFINE FUNCTION OVERWRITE fn::get_game_messages_by_game_id($game_id: string) {
    RETURN SELECT * FROM message
    WHERE source.type = 'Game'
    AND source.value = $game_id
    ORDER BY timestamp;
};

DEFINE FUNCTION OVERWRITE fn::get_messages_by_game_id($game_id: string) {
    RETURN SELECT * FROM message
    WHERE string::starts_with(subject, $game_id)
    ORDER BY timestamp;
};

DEFINE FUNCTION OVERWRITE fn::get_messages_by_tribute_id($tribute_id: string) {
    RETURN SELECT * FROM message
    WHERE string::contains(subject, $tribute_id)
    ORDER BY timestamp;
};

DEFINE TABLE OVERWRITE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD OVERWRITE script_name ON script_migration TYPE string;
DEFINE FIELD OVERWRITE executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
DEFINE TABLE OVERWRITE summary SCHEMALESS
    PERMISSIONS FOR
        CREATE, SELECT, UPDATE, DELETE WHERE $auth;
DEFINE FIELD OVERWRITE identifier ON summary;
DEFINE FIELD OVERWRITE day ON summary;
DEFINE FIELD OVERWRITE content ON summary;

DEFINE TABLE OVERWRITE summaries TYPE RELATION IN game OUT summary
PERMISSIONS FOR
        CREATE, SELECT, UPDATE, DELETE WHERE $auth;

DEFINE TABLE OVERWRITE tribute SCHEMALESS
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete WHERE created_by = $auth;

DEFINE FIELD OVERWRITE identifier ON tribute;
DEFINE FIELD OVERWRITE status ON tribute;
DEFINE FIELD OVERWRITE avatar ON tribute;
DEFINE FIELD OVERWRITE name ON tribute;
DEFINE FIELD OVERWRITE district ON tribute;
DEFINE FIELD OVERWRITE area ON tribute;
DEFINE FIELD OVERWRITE player_name ON tribute;
DEFINE FIELD OVERWRITE statistics ON tribute;
DEFINE FIELD OVERWRITE attributes ON tribute;
DEFINE FIELD OVERWRITE items ON tribute;
DEFINE FIELD OVERWRITE created_by ON tribute VALUE $auth READONLY;

DEFINE TABLE OVERWRITE owns TYPE RELATION IN tribute OUT item
PERMISSIONS
        FOR SELECT FULL
        FOR CREATE, UPDATE, DELETE WHERE $auth;

DEFINE TABLE OVERWRITE playing_in TYPE RELATION IN tribute OUT game ENFORCED
PERMISSIONS
        FOR SELECT FULL
        FOR CREATE, UPDATE, DELETE WHERE $auth;

DEFINE TABLE OVERWRITE user SCHEMAFULL
    -- Only allow users to query their own record, including their password.
    PERMISSIONS
        FOR select WHERE $auth
        FOR update, delete WHERE id = $auth.id;

DEFINE FIELD OVERWRITE username ON user TYPE string
    PERMISSIONS
        FOR select FULL
        FOR update WHERE id = $auth.id;
DEFINE FIELD OVERWRITE password on user TYPE string;

DEFINE INDEX OVERWRITE unique_username ON user FIELDS username UNIQUE;

DEFINE ACCESS OVERWRITE user ON DATABASE TYPE RECORD
    SIGNUP (
        CREATE user CONTENT {
            username: $username,
            password: crypto::argon2::generate($password)
        }
    )
    SIGNIN (
        SELECT * FROM user
        WHERE username = $username
        AND crypto::argon2::compare(password, $password)
    )
    WITH JWT
        ALGORITHM HS512
        KEY "6dxLjU0m8ZmAzaLEk_qAeMpeD5ZAjGYlCjlvDi5DcgdJLATIHuCReUu7CbGyCDhRSp3btd7Ezob7RPYe6fUtsA"
    DURATION FOR TOKEN 30d, FOR SESSION 1h;
